import React, { useState } from 'react';
import { Container, Navbar, Form, Button, InputGroup, Modal, ProgressBar, Alert } from 'react-bootstrap';
import { Clipboard } from 'react-bootstrap-icons';
import './App.css';

function App() {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [result, setResult] = useState(null); // null, 'safe', 'warning'

  const handleAnalyze = () => {
    if (!url) return;

    setLoading(true);
    setProgress(0);
    let current = 0;

    const interval = setInterval(() => {
      current += 10;
      setProgress(current);

      if (current >= 100) {
        clearInterval(interval);
        setLoading(false);
        // Simular resultado: 50% probabilidad de gambling
        const isGambling = Math.random() > 0.5;
        setResult(isGambling ? 'warning' : 'safe');
        setShowModal(true);
      }
    }, 200);
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(url);
  };

  return (
    <>
      <Navbar bg="success" variant="dark" className="px-4">
        <Navbar.Brand>
          <img
            src="" // Aquí colocas tu logo después
            width="30"
            height="30"
            className="d-inline-block align-top me-2"
            alt="Logo"
          />
          GAnalysis
        </Navbar.Brand>
      </Navbar>

      <Container className="my-5 text-center">
        <h2 className="mb-3">Analiza los anuncios de la web</h2>
        <p className="text-muted">
          Mediante web scraping analizamos los anuncios de la web que elijas para prevenir que tenga anuncios referentes al gambling.
        </p>

        <InputGroup className="mb-3 w-75 mx-auto">
          <Form.Control
            type="text"
            placeholder="Ingresa la URL del sitio web"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <Button variant="outline-secondary" onClick={handleCopy}>
            <Clipboard />
          </Button>
        </InputGroup>

        <Button variant="success" onClick={handleAnalyze}>ANALIZAR</Button>

        {loading && (
          <div className="mt-4 w-75 mx-auto">
            <ProgressBar now={progress} label={`${progress}%`} animated />
          </div>
        )}
      </Container>

      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Body className="text-center">
          {result === 'safe' ? (
            <Alert variant="success">
              <h4>✔️ Análisis Completado</h4>
              No se ha detectado ningún tipo de anuncio referente al gambling en la página que proporcionaste.
            </Alert>
          ) : (
            <Alert variant="danger">
              <h4>⚠️ Advertencia</h4>
              La página que proporcionaste posee anuncios que dan promoción al gambling, por lo que deberías tener cuidado.
            </Alert>
          )}
        </Modal.Body>
      </Modal>

      <footer className="bg-success text-white text-center py-3 mt-5">
        GAnalysis © 2025 | Proyecto de análisis de anuncios con IA
      </footer>
    </>
  );
}

export default App;
