import React, { useState } from 'react';
import {
  Container, Navbar, Form, Button, InputGroup,
  Modal, ProgressBar, Alert
} from 'react-bootstrap';
import { Clipboard } from 'react-bootstrap-icons';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [result, setResult] = useState(null); // null, 'safe', 'warning'

  const handleAnalyze = () => {
    if (!url) return;

    setLoading(true);
    setProgress(0);
    let current = 0;

    const interval = setInterval(() => {
      current += 10;
      setProgress(current);

      if (current >= 100) {
        clearInterval(interval);
        setLoading(false);
        const isGambling = Math.random() > 0.5;
        setResult(isGambling ? 'warning' : 'safe');
        setShowModal(true);
      }
    }, 200);
  };

  const handleCopy = () => {
    if (url) navigator.clipboard.writeText(url);
  };

  return (
    <>
      <Navbar bg="success" variant="dark" className="px-4">
        <Navbar.Brand className="d-flex align-items-center">
          <img
            src="" // Aquí puedes colocar tu logo
            width="32"
            height="32"
            className="me-2"
            alt="Logo"
          />
          <span className="fs-4 fw-semibold">GAnalysis</span>
        </Navbar.Brand>
      </Navbar>

      <Container className="my-5 text-center">
        <h2>Analiza los anuncios de la web</h2>
        <p className="mb-4">
          Mediante web scraping analizamos los anuncios de la web que elijas para prevenir que tenga anuncios referentes al gambling.
        </p>

        <InputGroup className="mb-3 w-75 mx-auto shadow-sm">
          <Form.Control
            placeholder="https://ejemplo.com"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <Button variant="outline-secondary" onClick={handleCopy}>
            <Clipboard />
          </Button>
        </InputGroup>

        <Button variant="success" onClick={handleAnalyze}>
          ANALIZAR
        </Button>

        {loading && (
          <div className="w-75 mx-auto mt-4">
            <ProgressBar animated now={progress} label={`${progress}%`} />
          </div>
        )}
      </Container>

      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Body className="text-center">
          {result === 'safe' ? (
            <Alert variant="success">
              <h4 className="mb-3">✔️ Análisis Completado</h4>
              No se ha detectado ningún tipo de anuncio referente al gambling en la página que proporcionaste.
            </Alert>
          ) : (
            <Alert variant="danger">
              <h4 className="mb-3">⚠️ Advertencia</h4>
              La página que proporcionaste posee anuncios que dan promoción al gambling, por lo que deberías tener cuidado.
            </Alert>
          )}
        </Modal.Body>
      </Modal>

      <footer>
        GAnalysis © 2025 | Desarrollado para detectar amenazas de anuncios relacionados con gambling
      </footer>
    </>
  );
}

export default App;
